export const api = {
  // Base Url
  baseUrl: "http://localhost:3002",

  // Endpoints
  readAllGames: () => `${api.baseUrl}/games`,

  // Requisições

  // Get
  buildApiGetRequest: (url) =>
    fetch(url, {
      method: "GET",
    }),

  // Post
  buildApiPostRequest: (url, body) =>
    fetch(url, {
      method: "POST",
      headers: new Headers({
        "Content-type": "application/json",
      }),
      body: JSON.stringify(body),
    }),
};
